//react
import React, { Component} from 'react';

//algorithms
import BubbleSort from './algorithms/bubblesort';

//css && components
import './App.css';
import Bar from './components/bar';    

//icons
import Play from '@material-ui/icons/PlayCircleOutlineRounded'
import Forward from '@material-ui/icons/SkipNextRounded'
import Backward from '@material-ui/icons/SkipPreviousRounded'
import RotateLeft from '@material-ui/icons/RotateLeftRounded'

class App extends Component {
    state = {
        array:[],
        arraySteps:[],
        colorKey:[],
        colorSteps:[],
        currentStep:0,
        count:20,
        algorithm:'bubble Sort',
        delay:500,
        timeouts:[],
    };
    ALGORITHMS = {
        'bubble Sort':BubbleSort,
    }
    componentDidMount() {
        this.generateRandomArray();
    }
    clearTimeouts = () =>{
        this.state.timeouts.forEach((timeout) => clearTimeout(timeout));
        this.setState({
            timeouts:[],
        });
    }
    start = () =>{
        let steps = this.state.arraySteps;
        let colorSteps = this.state.colorSteps;
        this.clearTimeouts();
        let timeouts = [];
        let i = 0;
        while(i<steps.length - this.state.currentStep){
            let timeout = setTimeout(() => {
                let currentStep = this.state.currentStep;
                this.setState({
                    array:steps[currentStep],
                    colorKey:colorSteps[currentStep],
                    currentStep:currentStep+1,
                });
                timeouts.push(timeout);
            },this.state.delay*i);
            i++;
        }
        this.setState({
            timeouts:timeouts

        });
    };

    generateRandomNumber = (min , max) =>{
        return Math.floor(Math.random()*(max - min) + min);

    }
    changeArray = (index,value) =>{
        let arr = this.state.array;
        arr[index] = value;

        this.setState({
            array:arr,
            arraySteps:[arr],
            currentStep:0 },()=>{
                this.generateSteps();
            });
    };
    generateSteps = () =>{
        let array = this.state.array.slice();
        let steps = this.state.arraySteps.slice();
        let colorSteps = this.state.colorSteps.slice();
        this.ALGORITHMS[this.state.algorithm](array,0,steps,colorSteps);
        this.setState({
            arraySteps:steps,
            cokorSteps:colorSteps
        });
    };
    clearColorKey = () =>{
        let blankKey = new Array(this.state.count).fill(0);
        this.setState({
            colorKey:blankKey,
            colorSteps:[blankKey]
        });
    };
    generateRandomArray = () =>{
        this.clearTimeouts();
        this.clearColorKey();
        const count = this.state.count;
        const tempArray = [];
        for(var i = 0;i<count;i++){
            tempArray.push(this.generateRandomNumber(50,200));
        }
        this.setState({
            array : tempArray,
            arraySteps : [tempArray],
            currentStep : 0
        },()=>{
            this.generateSteps();
        })
    }
    render() {
        let bars = this.state.array.map((value,index) => (

                <Bar key = {index}
                index = {index}
                length = {value}
                color = {0}
                changearray = {this.changeArray} 
                />
        ));
        let playButton;
        if(this.state.arraySteps === this.state.currentStep){
            playButton = (
                <button className="controller">
                    <RotateLeft/>
                </button>
            )
        }else{
            playButton = (
                <button className="controller" onClick={this.start}>
                    <Play/>
                </button>
            )
        }

        return (
                <div className='app'>
                <div className="frame">
                    <div className="barsDiv container card"> {bars} </div>
                </div>
                <div className="control-pannel">
                    <div className="control-buttons">
                    <button className="controller">
                        <Backward/>
                    </button>
                        {playButton}
                    <button className="controller">
                        <Forward/>
                    </button>
                    </div>
                </div>
                <div className="pannel"></div>
                </div>
                );
    }

}

export default App;
