import { useState } from 'react';
import './Bar.css';

function Bar({index , length , color}) {
    const [len,setLen] = useState(length)
    const colors = [
        ['rgba(61,90,241,0.5)','rgba(61,90,241,0.2)'],
        ['rgba(255,48,79,1)','rgba(255,48,79,0.5)'],
        ['rgba(131,232,90,1)','rgba(131,232,90,0.2)']
    ]

    const bottom = {
        transform : `translateY(${200 - length}px) rotateX(-90deg)`,
        backgroundColor:`${colors[color][0]}`,
        boxShadow:`5px 5px 50px 5px ${colors[color][1]}`,
        transition:'0.3s'
    }
    const front_back_right_left = {
        height : `${length}px`,
        transform: `translateY(${200 - length})px`,
        backgroundColor: `${colors[color][0]}`,
        boxShadow: `5px 5px 50px 5px ${colors[color][1]}`,
        transition: '0.3s' 
    }

    const handelChange = (e) =>{
        let val = e.target.value;
        if(val === ''){
            setLen(0)
        }
        else{
            val = parseInt(val);
            if(val >200){
                val = 200
            }else if(val < 50){
                val = 50;
            }
            setLen(val)
        
        }
       
    }

    // const barStyle = {
    //     height:length
    // }
    const inputStyle = {
        position: 'relative',
        top: Math.floor(length/2) - 10,
        width:length,
        left: -Math.floor(length/2) + 10,
        border:'none',
        background:'none',
    }


    return (
    <>  
        <div className="bar">
            <div className="side top"></div>
            <div className="side bottom" style={bottom}></div>
            <div className="side right">
                <div className="color-bar right-color-bar" style={front_back_right_left}>

                </div>
            </div>
            <div className="side left">
                <div className="color-bar left-color-bar" style={front_back_right_left}></div>
            </div>
            <div className="side front">
                <div className="color-bar front-color-bar"  style={front_back_right_left} ></div>
                <input
                    type="number" 
                    style={inputStyle} 
                    value = {len} 
                    className = "input"
                    onChange ={handelChange}/>
            </div>
            <div className="side back">
                <div className="color-bar back-color-bar" style={front_back_right_left}>
                </div>
            </div>
            <div className="quantity-nav">
                <div className="quantity-button quantity-up">

                </div>
                <div className="quantity-button quantity-down">
                    
                    </div>
            </div>
        </div>
    </>
    )
}

export default Bar;
